

# Importando bibliotecas
import numpy as np
from numpy import zeros, arange
import matplotlib.pyplot as plt
import control as ctl
from scipy.integrate import odeint
from scipy.optimize import minimize

"""# **Ajustando PID pela resposta ao degrau**"""

# Definindo dinamica da planta
def din_sis(y, t, u):
    # Definindo estados
    x1, x2, x3, x4 = y
    # Dinamica do sistema
    x1p = x2
    x2p = x3
    x3p = x4
    x4p = -4*x4 - 6*x3 - 4*x2 -1*x1 + u
    return [x1p, x2p, x3p, x4p]


# Parametros de simulacao
Ta = 1e-3  # peridodo de amostragem
Tsim = 15  # tempo de simulacao
kend = int(Tsim/Ta)

# Scopes
u = zeros(kend)
y = zeros(kend)
yp = zeros(kend)
ypp = zeros(kend)
yppp = zeros(kend)
y_ref = zeros(kend)

# Loop
for k in range(kend-1):

    # Entrada degrau apos 1 s
    if k*Ta > 1:
        u[k] = 1

    # Evoluindo a din. da planta
    x0 = [y[k], yp[k], ypp[k], yppp[k]]
    sol = odeint(din_sis, x0, [0.0, Ta], args=(u[k-200],))
    y[k + 1] = sol[:, 0][-1]
    yp[k + 1] = sol[:, 1][-1]
    ypp[k + 1] = sol[:, 2][-1]
    yppp[k + 1] = sol[:, 3][-1]

fig1 = plt.figure()
plt.plot(arange(0, Tsim, Ta), y, lw=2, label=r'$y$')
plt.xlabel('Tempo (s)')
plt.legend()
plt.grid()

plt.show()

fig2 = plt.figure()
plt.plot(arange(0, Tsim-Ta, Ta), u[0:-1], 'r--', lw=2, label=r'$u$ (rad/s)')
plt.xlabel('Tempo (s)')
plt.legend()
plt.grid()

plt.show()

"""Como temos um tempo de convergência de 11 segundos aproximadamente, nosso período pode ser no máximo de 1,1 segundos.

**Escolheremos 10 milisegundos.**

Podemos ver que há um atraso de aproximadamente 1 segundo (parametro L), e podemos medir o tempo de 63% da resposta para obter o parâmetro T.

Além disso, o ganho K pode ser medido como 1, uma vez que a resposta converte para a entrada, sem descasamento
"""

par_K = 1
par_L = 1
par_T = Ta*np.argmin(np.abs(y-0.63)) - 1

print(f"Parâmetro T: {par_T}")

"""![image.png](data:image/png;base64,)

A paryir da tabela acima podemos achar os parametros:
"""

kp_zn = 1.2*(par_T/(par_K*par_L))
ti_zn = 2*par_L
td_zn = 0.5*par_L

print(kp_zn, '\n', ti_zn, '\n', td_zn)

"""### *Simulando o controlador ajustado por Ziegler Nichols*"""

# Definindo dinamica da planta
def din_sis(y, t, u):
    # Definindo estados
    x1, x2, x3, x4 = y
    # Dinamica do sistema
    x1p = x2
    x2p = x3
    x3p = x4
    x4p = -4*x4 - 6*x3 - 4*x2 -1*x1 + u
    return [x1p, x2p, x3p, x4p]


# Parametros de simulacao
Ta = 0.01  # peridodo de amostragem
Tsim = 20  # tempo de simulacao
kend = int(Tsim/Ta)

# Parametros do controlador
Kp = kp_zn
Ti = ti_zn
Td = td_zn

# Scopes
u = zeros(kend)
y = zeros(kend)
yp = zeros(kend)
ypp = zeros(kend)
yppp = zeros(kend)
y_ref = zeros(kend)
e = zeros(kend)
int_e = zeros(kend)
der_e = zeros(kend)


# Loop
for k in range(kend-1):

    # Mudanca na referencia
    if k*Ta > 1:
        y_ref[k] = 1

    # Calculo do erro de rastreamento
    e[k] = y_ref[k] - y[k]
    int_e[k] = int_e[k-1] + e[k]*Ta
    if k*Ta > 2:
      #  Para não termos o efeito de derivada "infinita"
      der_e[k] = (e[k]-e[k-1])/Ta

    # Calculo da acao de controle a partir da eq. a dif.
    u[k] = Kp*e[k] + (Kp/Ti)*(int_e[k]) + (Kp*Td)*(der_e[k])

    # Evoluindo a din. da planta
    x0 = [y[k], yp[k], ypp[k], yppp[k]]
    sol = odeint(din_sis, x0, [0.0, Ta], args=(u[k-200],))
    y[k + 1] = sol[:, 0][-1]
    yp[k + 1] = sol[:, 1][-1]
    ypp[k + 1] = sol[:, 2][-1]
    yppp[k + 1] = sol[:, 3][-1]

fig1 = plt.figure()
plt.plot(arange(0, Tsim, Ta), y, lw=2, label=r'$y$')
plt.xlabel('Tempo (s)')
plt.legend()
plt.grid()

plt.show()

fig2 = plt.figure()
plt.plot(arange(0, Tsim-Ta, Ta), u[0:-1], 'r--', lw=2, label=r'$u$ (rad/s)')
plt.xlabel('Tempo (s)')
plt.legend()
plt.grid()

plt.show()

"""# **Ajustando PID por otimização**"""

MS = 20
ts = 8

csi_desejado = np.sqrt((np.log(MS/100)**2)/((np.pi**2)+(np.log(MS/100)**2)))
w_desejado = 4/(ts*csi_desejado)

print(csi_desejado, '\n', w_desejado)

# Definindo dinamica da planta
def din_sis(y, t, u):
    # Definindo estados
    x1, x2, x3, x4 = y
    # Dinamica do sistema
    x1p = x2
    x2p = x3
    x3p = x4
    x4p = -4*x4 - 6*x3 - 4*x2 -1*x1 + u
    return [x1p, x2p, x3p, x4p]


# Definindo dinamica de REFERENCIA
def din_sis_ref(y_ref, t, ref):
    global csi_desejado, w_desejado
    # Parametros desejados
    csi = csi_desejado
    omega_n = w_desejado

    # Definindo estados
    x1, x2 = y_ref
    # Dinamica do sistema
    x1p = x2
    x2p = (omega_n**2)*ref - 2*csi*omega_n*x2 - (omega_n**2)*x1
    return [x1p, x2p]


def sim_MF_J(x, y_ref, Ta, Tsim):
    # Parametros de simulacao
    kend = int(Tsim/Ta)

    # Parametros do controlador
    Kp = x[0]
    Ti = x[1]
    Td = x[2]

    # Scopes
    u = zeros(kend)
    y = zeros(kend)
    yp = zeros(kend)
    ypp = zeros(kend)
    yppp = zeros(kend)
    ref = zeros(kend)
    e = zeros(kend)
    em = zeros(kend)  # erro entre modelo de referencia e real
    int_e = zeros(kend)
    der_e = zeros(kend)

    # Loop
    for k in range(kend-1):

        # Mudanca na referencia
        if k*Ta > 1:
            ref[k] = 1

        # Calculo do erro de rastreamento
        e[k] = ref[k] - y[k]
        int_e[k] = int_e[k-1] + e[k-1]*Ta
        if k*Ta > 1.01:
          #  Para não termos o efeito de derivada "infinita"
          der_e[k] = (e[k]-e[k-1])/Ta

        # Calculo da acao de controle
        u[k] = Kp*e[k] + (Kp/Ti)*(int_e[k]) - (Kp*Td)*(yp[k])

        # Evoluindo a din. da planta
        x0 = [y[k], yp[k], ypp[k], yppp[k]]
        sol = odeint(din_sis, x0, [0.0, Ta], args=(u[k-200],))
        y[k + 1] = sol[:, 0][-1]
        yp[k + 1] = sol[:, 1][-1]
        ypp[k + 1] = sol[:, 2][-1]
        yppp[k + 1] = sol[:, 3][-1]

        # Diferenca modelo real e modelo de referencia
        em[k] = abs(y[k] - y_ref[k])

    return sum(em)


# Codigo principal
# Simulando modelo de referencia
# Parametros de simulacao
Ta = 0.01
Tsim = 20  # tempo de simulacao
kend = int(Tsim / Ta)
# Evolution modelo de referencia
# Scopes
ref = zeros(kend)
y_ref = zeros(kend)
yp_ref = zeros(kend)

# Loop de referencia
for k in range(kend - 1):

    # Gerando saida do modelo de referencia
    if k * Ta > 1:
        ref[k] = 1
        
    x0_ref = [y_ref[k], yp_ref[k]]
    sol_ref = odeint(din_sis_ref, x0_ref, [0.0, Ta], args=(ref[k - 200],))
    y_ref[k + 1] = sol_ref[:, 0][-1]
    yp_ref[k + 1] = sol_ref[:, 1][-1]


# Palpite inicial
Kp0 = 1
Ti0 = 1
Td0 = 1
sol0 = np.array([Kp0, Ti0, Td0])

# Buscando parametros otimos
res = minimize(sim_MF_J, sol0, method='nelder-mead', options={'xatol' : 1e08, 'disp': True}, args=(y_ref, Ta, Tsim))
print(res.x)

